package ua.pp.vbabich.oauth.providers;import org.junit.Test;import static org.junit.Assert.assertEquals;public class FBLoginTest {    @Test    public void testDecodeResponse() throws Exception {        String json = "{" +                    "\"id\":\"100001714431144\"" +                    ",\"first_name\":\"\\u0412\\u043e\\u043b\\u043e\\u0434\\u0438\\u043c\\u0438\\u0440\"" +                    ",\"gender\":\"male\"" +                    ",\"last_name\":\"\\u0411\\u0430\\u0431\\u0438\\u0447\"" +                    ",\"link\":\"https:\\/\\/www.facebook.com\\/app_scoped_user_id\\/100001714431144\\/\"" +                    ",\"locale\":\"uk_UA\"" +                    ",\"name\":\"\\u0412\\u043e\\u043b\\u043e\\u0434\\u0438\\u043c\\u0438\\u0440 \\u0411\\u0430\\u0431\\u0438\\u0447\"" +                    ",\"timezone\":3" +                    ",\"updated_time\":\"2016-01-03T11:14:58+0000\"" +                    ",\"verified\":true" +                "}";        FBLogin target = new FBLogin();        FBLogin.FB fb = target.decodeResponse(json);        assertEquals(fb.id, "100001714431144");        assertEquals(fb.first_name, "Володимир");        assertEquals(fb.last_name, "Бабич");        assertEquals(fb.name, "Володимир Бабич");    }}